    <Grid centered columns={2}>
        <Grid.Column>
            <Music/>
            <Header as="h2" textAlign="center">
                Congrats Cat and Jenny
            </Header>
            <video playsinline="playsinline" autoplay="autoplay" muted="muted" loop="loop">
