import React from 'react';
import {
    Button,
    Form,
    Grid,
    Header,
    Message,
    Segment,
    Icon,
    Image
} from 'semantic-ui-react';
import myImage from './download.jpeg';
//import { CarouselProvider, Slider, Slide, ButtonBack, ButtonNext } from 'pure-react-carousel';
import 'pure-react-carousel/dist/react-carousel.es.css';
import Slider from 'infinite-react-carousel';

const elementsinitial = [];
var elements = [];
for (var x = 0; x < 1001; x++) {
    elementsinitial.push(x);
}

for (var x = 2; x < 1001; x++) {
    elements[x] = "" + x + " Wishes for a happy 2021 for Cat and Jenny 👫 🎉 💍! ";
}

const SimpleSlider = () => (
    <Slider dots>
      <div>
        <h3>1</h3>
      </div>
      <div>
        <h3>2</h3>
      </div>
      <div>
        <h3>3</h3>
      </div>
      <div>
        <h3>4</h3>
      </div>
      <div>
        <h3>5</h3>
      </div>
    </Slider>
  );

/*elements =  [
   {
     name: "🎉 William",
     location: "👫 Lagos",
     car: "💍 Honda"
   },
   {
     name: "😃 Chris",
     location: "🏘️ Moon",
     car: "🚘 Tesla"
   },
   {
     name: " 😃 Rose",
     location: "🏘️ Venice",
     car: "🚘 Pagani"
   },
   {
     name: "😃 Mike",
     location: "🏘️ Milan",
     car: "🚘 Rolls Royce"
   },
   {
     name: "😃 Liz",
     location: "🏘️ Beirut",
     car: "🚘 Mercedes"
   }
 ];*/


export default () => (
    <Grid centered columns={2}>
        <Grid.Column>
            <Header as="h2" textAlign="center">
                Congrats Cat and Jenny
      </Header>
      SimpleSlider/>
            <Segment>
                <Image src={myImage} fluid />
                <CarouselProvider
        naturalSlideWidth={100}
        naturalSlideHeight={125}
        totalSlides={3}
      >
        <Slider>
          <Slide index={0}>I am the first Slide.</Slide>
          <Slide index={1}>I am the second Slide.</Slide>
          <Slide index={2}>I am the third Slide.</Slide>
        </Slider>
        <ButtonBack>Back</ButtonBack>
        <ButtonNext>Next</ButtonNext>
      </CarouselProvider>

                <Form size="large">
                    <Message
                        success
                        icon='thumbs up'
                        header='Nice job!'
                        content='Your profile is complete.'
                    />
                    <div>
                    </div>
                    {elements.map((user, index) => (
                        <div key={index} class="test">
                            <h3>{user}</h3>
                            <p>{user.location}</p>
                            <p>{user.car}</p>
                        </div>
                    ))}
                </Form>
                <SimpleSlider/>
            </Segment>
            <Message>
                1000 wishes for a happy 2021!
      </Message>
        </Grid.Column>
    </Grid>
);
